import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { z } from "zod";
import { insertJobSchema, type Job } from "@shared/schema";
import { gnewsService } from "./services/gnews";
import { nlpService } from "./services/nlp";
import { videoService } from "./services/video";
import { youtubeService } from "./services/youtube";
import { scheduler } from "./jobs/scheduler";

export async function registerRoutes(app: Express): Promise<Server> {
  // Health check
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", timestamp: new Date().toISOString() });
  });

  // Metrics endpoint
  app.get("/api/metrics", async (req, res) => {
    try {
      const metrics = await storage.getMetrics();
      res.json(metrics);
    } catch (error) {
      console.error("Error fetching metrics:", error);
      res.status(500).json({ message: "Failed to fetch metrics" });
    }
  });

  // Jobs CRUD
  app.get("/api/jobs", async (req, res) => {
    try {
      const page = parseInt(req.query.page as string) || 1;
      const limit = parseInt(req.query.limit as string) || 10;
      const status = req.query.status as string;
      const topic = req.query.topic as string;

      const filters = {
        ...(status && { status }),
        ...(topic && { topic }),
      };

      const jobs = await storage.getJobs({ page, limit, filters });
      res.json(jobs);
    } catch (error) {
      console.error("Error fetching jobs:", error);
      res.status(500).json({ message: "Failed to fetch jobs" });
    }
  });

  app.get("/api/jobs/:id", async (req, res) => {
    try {
      const job = await storage.getJobById(req.params.id);
      if (!job) {
        return res.status(404).json({ message: "Job not found" });
      }
      res.json(job);
    } catch (error) {
      console.error("Error fetching job:", error);
      res.status(500).json({ message: "Failed to fetch job" });
    }
  });

  app.post("/api/jobs", async (req, res) => {
    try {
      const jobData = insertJobSchema.parse(req.body);
      const job = await storage.createJob(jobData);

      // Start processing the job asynchronously
      processJob(job.id).catch(error => {
        console.error(`Error processing job ${job.id}:`, error);
        storage.updateJobStatus(job.id, "failed", error.message);
      });

      res.status(201).json(job);
    } catch (error) {
      console.error("Error creating job:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid job data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create job" });
    }
  });

  app.post("/api/jobs/:id/publish", async (req, res) => {
    try {
      const job = await storage.getJobById(req.params.id);
      if (!job) {
        return res.status(404).json({ message: "Job not found" });
      }

      if (job.status !== "completed") {
        return res.status(400).json({ message: "Job must be completed before publishing" });
      }

      const video = await storage.getVideoByJobId(job.id);
      if (!video) {
        return res.status(400).json({ message: "No video found for job" });
      }

      const publication = await youtubeService.publishVideo(job, video);
      res.json(publication);
    } catch (error) {
      console.error("Error publishing job:", error);
      res.status(500).json({ message: "Failed to publish job" });
    }
  });

  app.delete("/api/jobs/:id", async (req, res) => {
    try {
      await storage.deleteJob(req.params.id);
      res.status(204).send();
    } catch (error) {
      console.error("Error deleting job:", error);
      res.status(500).json({ message: "Failed to delete job" });
    }
  });

  // System status
  app.get("/api/system/status", async (req, res) => {
    try {
      const status = {
        gnews: await gnewsService.checkStatus(),
        nlp: await nlpService.checkStatus(),
        video: await videoService.checkStatus(),
        youtube: await youtubeService.checkStatus(),
      };
      res.json(status);
    } catch (error) {
      console.error("Error checking system status:", error);
      res.status(500).json({ message: "Failed to check system status" });
    }
  });

  // Manual triggers
  app.post("/api/system/sync", async (req, res) => {
    try {
      await scheduler.triggerNewsSync();
      res.json({ message: "News sync triggered successfully" });
    } catch (error) {
      console.error("Error triggering sync:", error);
      res.status(500).json({ message: "Failed to trigger sync" });
    }
  });

  app.post("/api/system/pause", async (req, res) => {
    try {
      scheduler.pauseAutomation();
      res.json({ message: "Automation paused successfully" });
    } catch (error) {
      console.error("Error pausing automation:", error);
      res.status(500).json({ message: "Failed to pause automation" });
    }
  });

  app.post("/api/system/resume", async (req, res) => {
    try {
      scheduler.resumeAutomation();
      res.json({ message: "Automation resumed successfully" });
    } catch (error) {
      console.error("Error resuming automation:", error);
      res.status(500).json({ message: "Failed to resume automation" });
    }
  });

  const httpServer = createServer(app);

  // Start the scheduler
  scheduler.start();

  return httpServer;
}

async function processJob(jobId: string): Promise<void> {
  try {
    await storage.updateJobStatus(jobId, "running", null, 10);

    // Step 1: Fetch article
    console.log(`[${jobId}] Fetching article from GNews API`);
    const job = await storage.getJobById(jobId);
    if (!job) throw new Error("Job not found");

    const articles = await gnewsService.fetchArticles(job.topic, job.language);
    if (!articles.length) throw new Error("No articles found");

    const article = await storage.createArticle({
      jobId,
      source: "gnews",
      url: articles[0].url,
      title: articles[0].title,
      content: articles[0].description,
      contentHash: generateHash(articles[0].description),
      mediaUrl: articles[0].image,
      publishedAt: new Date(articles[0].publishedAt),
    });

    await storage.updateJobStatus(jobId, "running", null, 25);

    // Step 2: Generate summary
    console.log(`[${jobId}] Generating summary`);
    const summaryResult = await nlpService.generateSummary(
      article.title,
      article.content,
      job.targetLength
    );

    const summary = await storage.createSummary({
      jobId,
      text: summaryResult.text,
      wordCount: summaryResult.wordCount,
      language: job.language,
      qualityFlags: summaryResult.qualityFlags || {},
    });

    await storage.updateJobStatus(jobId, "running", null, 50);

    // Step 3: Generate TTS
    console.log(`[${jobId}] Converting text to speech`);
    const audioResult = await nlpService.generateTTS(summary.text, job.language);

    const audio = await storage.createAudio({
      jobId,
      url: audioResult.url,
      duration: audioResult.duration,
      sampleRate: audioResult.sampleRate,
      format: audioResult.format,
      size: audioResult.size,
    });

    await storage.updateJobStatus(jobId, "running", null, 75);

    // Step 4: Generate video
    console.log(`[${jobId}] Rendering video`);
    const videoResult = await videoService.renderVideo({
      summary: summary.text,
      audioUrl: audio.url,
      duration: audio.duration,
      images: article.mediaUrl ? [article.mediaUrl] : [],
      title: article.title,
    });

    const video = await storage.createVideo({
      jobId,
      urlMp4: videoResult.urlMp4,
      urlSrt: videoResult.urlSrt,
      urlThumb: videoResult.urlThumb,
      width: videoResult.width,
      height: videoResult.height,
      duration: videoResult.duration,
      size: videoResult.size,
    });

    await storage.updateJobStatus(jobId, "completed", null, 100);

    // Step 5: Auto-publish if enabled
    if (job.autoPublish) {
      console.log(`[${jobId}] Auto-publishing to YouTube`);
      await youtubeService.publishVideo(job, video);
    }

    console.log(`[${jobId}] Job completed successfully`);
  } catch (error) {
    console.error(`[${jobId}] Job failed:`, error);
    await storage.updateJobStatus(jobId, "failed", error instanceof Error ? error.message : "Unknown error");
  }
}

function generateHash(content: string): string {
  // Simple hash function - in production, use crypto
  let hash = 0;
  for (let i = 0; i < content.length; i++) {
    const char = content.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32-bit integer
  }
  return Math.abs(hash).toString(16);
}

server/storage.ts.content:
import { 
  jobs, 
  articles, 
  summaries, 
  audioFiles, 
  videos, 
  publications, 
  users,
  type Job, 
  type InsertJob,
  type Article,
  type InsertArticle,
  type Summary,
  type InsertSummary,
  type AudioFile,
  type InsertAudioFile,
  type Video,
  type InsertVideo,
  type Publication,
  type InsertPublication,
  type User, 
  type InsertUser 
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, gte, lte, inArray, sql, count } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Job methods
  getJobs(params: { page: number; limit: number; filters?: any }): Promise<{ data: Job[]; total: number; page: number; limit: number; totalPages: number }>;
  getJobById(id: string): Promise<Job | undefined>;
  getJobsByTopic(topic: string, daysSince?: number): Promise<Job[]>;
  getStuckJobs(minutesThreshold: number): Promise<Job[]>;
  createJob(job: InsertJob): Promise<Job>;
  updateJobStatus(id: string, status: string, error?: string | null, progress?: number): Promise<void>;
  deleteJob(id: string): Promise<void>;
  deleteJobsOlderThan(date: Date): Promise<number>;

  // Article methods
  getArticleByJobId(jobId: string): Promise<Article | undefined>;
  createArticle(article: InsertArticle): Promise<Article>;
  getExistingArticleHashes(hashes: string[]): Promise<string[]>;

  // Summary methods
  getSummaryByJobId(jobId: string): Promise<Summary | undefined>;
  createSummary(summary: InsertSummary): Promise<Summary>;

  // Audio methods
  getAudioByJobId(jobId: string): Promise<AudioFile | undefined>;
  createAudio(audio: InsertAudioFile): Promise<AudioFile>;

  // Video methods
  getVideoByJobId(jobId: string): Promise<Video | undefined>;
  createVideo(video: InsertVideo): Promise<Video>;

  // Publication methods
  createPublication(publication: InsertPublication): Promise<Publication>;

  // Metrics
  getMetrics(): Promise<{
    jobsToday: number;
    successRate: number;
    avgRenderTime: string;
    published: number;
    activeJobs: number;
    totalJobs: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getJobs(params: { page: number; limit: number; filters?: any }) {
    const { page, limit, filters = {} } = params;
    const offset = (page - 1) * limit;

    // Build where conditions
    let whereConditions = [];

    if (filters.status) {
      whereConditions.push(eq(jobs.status, filters.status));
    }
    if (filters.topic) {
      whereConditions.push(sql`${jobs.topic} ILIKE ${`%${filters.topic}%`}`);
    }
    if (filters.language) {
      whereConditions.push(eq(jobs.language, filters.language));
    }
    if (filters.dateFrom) {
      whereConditions.push(gte(jobs.createdAt, new Date(filters.dateFrom)));
    }
    if (filters.dateTo) {
      whereConditions.push(lte(jobs.createdAt, new Date(filters.dateTo)));
    }

    const whereClause = whereConditions.length > 0 ? and(...whereConditions) : undefined;

    // Get total count
    const [{ count: totalCount }] = await db
      .select({ count: count() })
      .from(jobs)
      .where(whereClause);

    // Get paginated jobs
    const jobsData = await db
      .select()
      .from(jobs)
      .where(whereClause)
      .orderBy(desc(jobs.createdAt))
      .limit(limit)
      .offset(offset);

    return {
      data: jobsData,
      total: totalCount,
      page,
      limit,
      totalPages: Math.ceil(totalCount / limit),
    };
  }

  async getJobById(id: string): Promise<Job | undefined> {
    const [job] = await db.select().from(jobs).where(eq(jobs.id, id));
    return job || undefined;
  }

  async getJobsByTopic(topic: string, daysSince = 1): Promise<Job[]> {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - daysSince);

    return await db
      .select()
      .from(jobs)
      .where(and(
        eq(jobs.topic, topic),
        gte(jobs.createdAt, cutoffDate)
      ))
      .orderBy(desc(jobs.createdAt));
  }

  async getStuckJobs(minutesThreshold: number): Promise<Job[]> {
    const cutoffTime = new Date();
    cutoffTime.setMinutes(cutoffTime.getMinutes() - minutesThreshold);

    return await db
      .select()
      .from(jobs)
      .where(and(
        eq(jobs.status, "running"),
        lte(jobs.updatedAt, cutoffTime)
      ));
  }

  async createJob(job: InsertJob): Promise<Job> {
    const [newJob] = await db
      .insert(jobs)
      .values({
        ...job,
        createdAt: new Date(),
        updatedAt: new Date(),
      })
      .returning();
    return newJob;
  }

  async updateJobStatus(id: string, status: string, error?: string | null, progress?: number): Promise<void> {
    const updateData: any = {
      status,
      updatedAt: new Date(),
    };

    if (error !== undefined) {
      updateData.error = error;
    }
    if (progress !== undefined) {
      updateData.progress = progress;
    }

    await db
      .update(jobs)
      .set(updateData)
      .where(eq(jobs.id, id));
  }

  async deleteJob(id: string): Promise<void> {
    await db.delete(jobs).where(eq(jobs.id, id));
  }

  async deleteJobsOlderThan(date: Date): Promise<number> {
    const result = await db
      .delete(jobs)
      .where(lte(jobs.createdAt, date))
      .returning({ id: jobs.id });

    return result.length;
  }

  async getArticleByJobId(jobId: string): Promise<Article | undefined> {
    const [article] = await db.select().from(articles).where(eq(articles.jobId, jobId));
    return article || undefined;
  }

  async createArticle(article: InsertArticle): Promise<Article> {
    const [newArticle] = await db
      .insert(articles)
      .values({
        ...article,
        createdAt: new Date(),
      })
      .returning();
    return newArticle;
  }

  async getExistingArticleHashes(hashes: string[]): Promise<string[]> {
    if (hashes.length === 0) return [];

    const existing = await db
      .select({ contentHash: articles.contentHash })
      .from(articles)
      .where(inArray(articles.contentHash, hashes));

    return existing.map(a => a.contentHash);
  }

  async getSummaryByJobId(jobId: string): Promise<Summary | undefined> {
    const [summary] = await db.select().from(summaries).where(eq(summaries.jobId, jobId));
    return summary || undefined;
  }

  async createSummary(summary: InsertSummary): Promise<Summary> {
    const [newSummary] = await db
      .insert(summaries)
      .values({
        ...summary,
        createdAt: new Date(),
      })
      .returning();
    return newSummary;
  }

  async getAudioByJobId(jobId: string): Promise<AudioFile | undefined> {
    const [audio] = await db.select().from(audioFiles).where(eq(audioFiles.jobId, jobId));
    return audio || undefined;
  }

  async createAudio(audio: InsertAudioFile): Promise<AudioFile> {
    const [newAudio] = await db
      .insert(audioFiles)
      .values({
        ...audio,
        createdAt: new Date(),
      })
      .returning();
    return newAudio;
  }

  async getVideoByJobId(jobId: string): Promise<Video | undefined> {
    const [video] = await db.select().from(videos).where(eq(videos.jobId, jobId));
    return video || undefined;
  }

  async createVideo(video: InsertVideo): Promise<Video> {
    const [newVideo] = await db
      .insert(videos)
      .values({
        ...video,
        createdAt: new Date(),
      })
      .returning();
    return newVideo;
  }

  async createPublication(publication: InsertPublication): Promise<Publication> {
    const [newPublication] = await db
      .insert(publications)
      .values({
        ...publication,
        createdAt: new Date(),
      })
      .returning();
    return newPublication;
  }

  async getMetrics() {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    // Jobs today
    const [{ count: jobsToday }] = await db
      .select({ count: count() })
      .from(jobs)
      .where(gte(jobs.createdAt, today));

    // Total jobs
    const [{ count: totalJobs }] = await db
      .select({ count: count() })
      .from(jobs);

    // Success rate (last 30 days)
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    const [{ count: totalRecent }] = await db
      .select({ count: count() })
      .from(jobs)
      .where(gte(jobs.createdAt, thirtyDaysAgo));

    const [{ count: successfulRecent }] = await db
      .select({ count: count() })
      .from(jobs)
      .where(and(
        gte(jobs.createdAt, thirtyDaysAgo),
        eq(jobs.status, "completed")
      ));

    const successRate = totalRecent > 0 ? (successfulRecent / totalRecent) * 100 : 0;

    // Active jobs
    const [{ count: activeJobs }] = await db
      .select({ count: count() })
      .from(jobs)
      .where(eq(jobs.status, "running"));

    // Published today
    const [{ count: published }] = await db
      .select({ count: count() })
      .from(publications)
      .where(gte(publications.createdAt, today));

    return {
      jobsToday,
      successRate: Math.round(successRate * 10) / 10,
      avgRenderTime: "2.4m", // This would need actual calculation from job timestamps
      published,
      activeJobs,
      totalJobs,
    };
  }
}

export const storage = new DatabaseStorage();

