import cron from 'node-cron';
import { gnewsService } from '../services/gnews';
import { storage } from '../storage';
import type { InsertJob } from '@shared/schema';

class JobScheduler {
  private cronJobs: Map<string, cron.ScheduledTask> = new Map();
  private isPaused = false;
  private defaultTopics = [
    "technology",
    "artificial intelligence",
    "climate change", 
    "economy",
    "health",
    "science",
    "global politics"
  ];

  start(): void {
    console.log("Starting job scheduler...");

    // Daily news sync at 8 AM, 2 PM, and 8 PM UTC
    const dailySync = cron.schedule('0 8,14,20 * * *', async () => {
      if (!this.isPaused) {
        await this.performDailySync();
      }
    }, {
      scheduled: true,
      timezone: "UTC"
    });

    this.cronJobs.set('dailySync', dailySync);

    // Health check every 15 minutes
    const healthCheck = cron.schedule('*/15 * * * *', async () => {
      if (!this.isPaused) {
        await this.performHealthCheck();
      }
    }, {
      scheduled: true,
      timezone: "UTC"
    });

    this.cronJobs.set('healthCheck', healthCheck);

    // Cleanup old jobs daily at midnight
    const cleanup = cron.schedule('0 0 * * *', async () => {
      await this.cleanupOldJobs();
    }, {
      scheduled: true,
      timezone: "UTC"
    });

    this.cronJobs.set('cleanup', cleanup);

    console.log("Job scheduler started successfully");
  }

  stop(): void {
    console.log("Stopping job scheduler...");
    this.cronJobs.forEach((job, name) => {
      job.destroy();
      console.log(`Stopped cron job: ${name}`);
    });
    this.cronJobs.clear();
    console.log("Job scheduler stopped");
  }

  pauseAutomation(): void {
    this.isPaused = true;
    console.log("Automation paused");
  }

  resumeAutomation(): void {
    this.isPaused = false;
    console.log("Automation resumed");
  }

  async triggerNewsSync(): Promise<void> {
    console.log("Manual news sync triggered");
    await this.performDailySync();
  }

  private async performDailySync(): Promise<void> {
    try {
      console.log("Starting daily news sync...");

      const syncPromises = this.defaultTopics.map(async (topic) => {
        try {
          // Check if we already have recent jobs for this topic
          const recentJobs = await storage.getJobsByTopic(topic, 1); // Get jobs from last 1 day
          if (recentJobs.length >= 3) {
            console.log(`Skipping ${topic} - already has ${recentJobs.length} recent jobs`);
            return;
          }

          // Fetch trending articles for this topic
          const articles = await gnewsService.fetchArticles(topic, "en", 5);

          if (articles.length === 0) {
            console.log(`No articles found for topic: ${topic}`);
            return;
          }

          // Get existing article hashes to avoid duplicates
          const existingHashes = await storage.getExistingArticleHashes(articles.map(a => 
            this.generateContentHash(a.description)
          ));

          // Filter out duplicates
          const newArticles = articles.filter(article => 
            !existingHashes.includes(this.generateContentHash(article.description))
          );

          if (newArticles.length === 0) {
            console.log(`No new articles for topic: ${topic}`);
            return;
          }

          // Create jobs for new articles (limit to 2 per topic per sync)
          const articlesToProcess = newArticles.slice(0, 2);

          for (const article of articlesToProcess) {
            const jobData: InsertJob = {
              topic: topic,
              language: "en",
              targetLength: this.getRandomTargetLength(),
              autoPublish: true,
              status: "queued",
              progress: 0,
              steps: [],
            };

            const job = await storage.createJob(jobData);
            console.log(`Created job ${job.id} for topic: ${topic}`);

            // Add small delay between job creations
            await new Promise(resolve => setTimeout(resolve, 1000));
          }

        } catch (error) {
          console.error(`Error processing topic ${topic}:`, error);
        }
      });

      await Promise.allSettled(syncPromises);
      console.log("Daily news sync completed");

    } catch (error) {
      console.error("Error in daily sync:", error);
    }
  }

  private async performHealthCheck(): Promise<void> {
    try {
      // Check for stuck jobs (running for more than 30 minutes)
      const stuckJobs = await storage.getStuckJobs(30); // 30 minutes

      for (const job of stuckJobs) {
        console.log(`Found stuck job: ${job.id}, resetting to failed`);
        await storage.updateJobStatus(job.id, "failed", "Job timed out");
      }

      // Log system stats
      const metrics = await storage.getMetrics();
      console.log(`Health check - Active jobs: ${metrics.activeJobs}, Success rate: ${metrics.successRate}%`);

    } catch (error) {
      console.error("Error in health check:", error);
    }
  }

  private async cleanupOldJobs(): Promise<void> {
    try {
      console.log("Starting job cleanup...");

      // Delete jobs older than 30 days
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - 30);

      const deletedCount = await storage.deleteJobsOlderThan(cutoffDate);
      console.log(`Cleaned up ${deletedCount} old jobs`);

    } catch (error) {
      console.error("Error in cleanup:", error);
    }
  }

  private generateContentHash(content: string): string {
    // Simple hash function - in production, use crypto
    let hash = 0;
    for (let i = 0; i < content.length; i++) {
      const char = content.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return Math.abs(hash).toString(16);
  }

  private getRandomTargetLength(): number {
    const lengths = [60, 90, 120]; // seconds
    return lengths[Math.floor(Math.random() * lengths.length)];
  }
}

export const scheduler = new JobScheduler();

server/services/gnews.ts.content:
interface GNewsArticle {
  title: string;
  description: string;
  url: string;
  image: string | null;
  publishedAt: string;
  source: {
    name: string;
    url: string;
  };
}

interface GNewsResponse {
  totalArticles: number;
  articles: GNewsArticle[];
}

class GNewsService {
  private readonly apiKey: string;
  private readonly baseUrl = "https://gnews.io/api/v4";
  private readonly cache = new Map<string, { data: GNewsArticle[]; timestamp: number }>();
  private readonly cacheTimeout = 5 * 60 * 1000; // 5 minutes

  constructor() {
    this.apiKey = process.env.GNEWS_API_KEY || process.env.API_KEY || "";
    if (!this.apiKey) {
      console.warn("GNews API key not found. Set GNEWS_API_KEY environment variable.");
    }
  }

  async checkStatus(): Promise<{ status: string; message?: string }> {
    try {
      const response = await fetch(`${this.baseUrl}/top-headlines?lang=en&max=1&token=${this.apiKey}`);
      if (response.ok) {
        return { status: "operational" };
      } else {
        return { status: "degraded", message: `HTTP ${response.status}` };
      }
    } catch (error) {
      return { status: "down", message: error instanceof Error ? error.message : "Unknown error" };
    }
  }

  async fetchArticles(topic: string, language = "en", maxResults = 10): Promise<GNewsArticle[]> {
    const cacheKey = `${topic}-${language}-${maxResults}`;
    const cached = this.cache.get(cacheKey);

    if (cached && Date.now() - cached.timestamp < this.cacheTimeout) {
      return cached.data;
    }

    if (!this.apiKey) {
      throw new Error("GNews API key not configured");
    }

    try {
      const params = new URLSearchParams({
        q: topic,
        lang: language,
        max: maxResults.toString(),
        token: this.apiKey,
      });

      const response = await fetch(`${this.baseUrl}/search?${params}`);

      if (!response.ok) {
        throw new Error(`GNews API error: ${response.status} ${response.statusText}`);
      }

      const data: GNewsResponse = await response.json();

      // Filter out articles without descriptions or images
      const filteredArticles = data.articles.filter(article => 
        article.description && article.description.length > 50
      );

      this.cache.set(cacheKey, { data: filteredArticles, timestamp: Date.now() });
      return filteredArticles;
    } catch (error) {
      console.error("Error fetching from GNews:", error);
      throw error;
    }
  }

  async fetchTrendingTopics(language = "en"): Promise<string[]> {
    if (!this.apiKey) {
      throw new Error("GNews API key not configured");
    }

    try {
      const params = new URLSearchParams({
        lang: language,
        max: "50",
        token: this.apiKey,
      });

      const response = await fetch(`${this.baseUrl}/top-headlines?${params}`);

      if (!response.ok) {
        throw new Error(`GNews API error: ${response.status} ${response.statusText}`);
      }

      const data: GNewsResponse = await response.json();

      // Extract topics from titles (simplified approach)
      const topics = new Set<string>();
      data.articles.forEach(article => {
        const words = article.title.toLowerCase().split(/\s+/);
        words.forEach(word => {
          if (word.length > 4 && !['this', 'that', 'with', 'from', 'they', 'have', 'will', 'been', 'says'].includes(word)) {
            topics.add(word);
          }
        });
      });

      return Array.from(topics).slice(0, 10);
    } catch (error) {
      console.error("Error fetching trending topics:", error);
      throw error;
    }
  }
}

export const gnewsService = new GNewsService();

server/services/nlp.ts.content:
interface SummaryResult {
  text: string;
  wordCount: number;
  qualityFlags?: Record<string, any>;
}

interface TTSResult {
  url: string;
  duration: number;
  sampleRate: number;
  format: string;
  size: number;
}

class NLPService {
  private readonly summarizerUrl: string;
  private readonly ttsUrl: string;

  constructor() {
    this.summarizerUrl = process.env.SUMMARIZER_URL || "https://api.huggingface.co/models/facebook/bart-large-cnn";
    this.ttsUrl = process.env.TTS_URL || "http://localhost:8001";
  }

  async checkStatus(): Promise<{ status: string; message?: string }> {
    try {
      // Check summarizer
      const summarizerResponse = await fetch(`${this.summarizerUrl}/health`, { 
        method: 'GET',
        timeout: 5000 
      }).catch(() => ({ ok: false, status: 500 }));

      // Check TTS service
      const ttsResponse = await fetch(`${this.ttsUrl}/health`, {
        method: 'GET',
        timeout: 5000
      }).catch(() => ({ ok: false, status: 500 }));

      if (summarizerResponse.ok && ttsResponse.ok) {
        return { status: "operational" };
      } else if (summarizerResponse.ok || ttsResponse.ok) {
        return { status: "degraded", message: "One service is down" };
      } else {
        return { status: "down", message: "Both services unavailable" };
      }
    } catch (error) {
      return { status: "down", message: error instanceof Error ? error.message : "Unknown error" };
    }
  }

  async generateSummary(title: string, content: string, targetLengthSeconds = 90): Promise<SummaryResult> {
    try {
      // Estimate target word count (average 150 words per minute for speech)
      const targetWords = Math.round((targetLengthSeconds / 60) * 150 * 0.8); // 80% to account for pauses

      const payload = {
        inputs: `Summarize this news article in approximately ${targetWords} words:\n\nTitle: ${title}\n\nContent: ${content}`,
        parameters: {
          max_length: Math.min(targetWords + 20, 512),
          min_length: Math.max(targetWords - 20, 50),
          do_sample: false,
          early_stopping: true,
        },
      };

      const response = await fetch(this.summarizerUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.HUGGINGFACE_API_KEY || process.env.API_KEY || ''}`,
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error(`Summarizer API error: ${response.status} ${response.statusText}`);
      }

      const result = await response.json();
      let summaryText = '';

      if (Array.isArray(result) && result[0]?.summary_text) {
        summaryText = result[0].summary_text;
      } else if (result.summary_text) {
        summaryText = result.summary_text;
      } else if (Array.isArray(result) && result[0]?.generated_text) {
        summaryText = result[0].generated_text;
      } else {
        throw new Error('Invalid response format from summarizer');
      }

      // Clean up the summary
      summaryText = this.cleanSummaryText(summaryText);
      const wordCount = summaryText.split(/\s+/).length;

      // Quality checks
      const qualityFlags = {
        length_appropriate: wordCount >= targetWords * 0.7 && wordCount <= targetWords * 1.3,
        has_key_info: summaryText.toLowerCase().includes(title.split(' ')[0].toLowerCase()),
        no_repetition: !this.hasExcessiveRepetition(summaryText),
      };

      return {
        text: summaryText,
        wordCount,
        qualityFlags,
      };
    } catch (error) {
      console.error("Error generating summary:", error);
      throw error;
    }
  }

  async generateTTS(text: string, language = "en", voiceId = "default"): Promise<TTSResult> {
    try {
      // Normalize text for speech
      const normalizedText = this.normalizeTextForSpeech(text);

      const payload = {
        text: normalizedText,
        voice_id: voiceId,
        language: language,
        speed: 1.0,
        sample_rate: 22050,
      };

      const response = await fetch(`${this.ttsUrl}/tts`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error(`TTS API error: ${response.status} ${response.statusText}`);
      }

      const result = await response.json();

      return {
        url: result.audio_url,
        duration: result.duration,
        sampleRate: result.sample_rate,
        format: result.format || 'wav',
        size: result.size || 0,
      };
    } catch (error) {
      console.error("Error generating TTS:", error);
      throw error;
    }
  }

  private cleanSummaryText(text: string): string {
    // Remove common artifacts and normalize
    return text
      .replace(/^(Summary:|Article:|News:)\s*/i, '')
      .replace(/\s+/g, ' ')
      .trim()
      // Ensure proper sentence ending
      .replace(/[.!?]\s*$/, '') + '.';
  }

  private normalizeTextForSpeech(text: string): string {
    return text
      // Expand common abbreviations
      .replace(/\bUS\b/g, 'United States')
      .replace(/\bUK\b/g, 'United Kingdom')
      .replace(/\bAI\b/g, 'A I')
      .replace(/\bAPI\b/g, 'A P I')
      .replace(/\bCEO\b/g, 'C E O')
      .replace(/\bIPO\b/g, 'I P O')
      // Add pauses after periods
      .replace(/\.\s+/g, '. ... ')
      // Handle numbers
      .replace(/\$(\d+)([bB]illion)/g, '$1 billion dollars')
      .replace(/\$(\d+)([mM]illion)/g, '$1 million dollars')
      .replace(/(\d+)%/g, '$1 percent');
  }

  private hasExcessiveRepetition(text: string): boolean {
    const sentences = text.split(/[.!?]+/).filter(s => s.trim().length > 0);
    const uniqueSentences = new Set(sentences.map(s => s.trim().toLowerCase()));
    return uniqueSentences.size < sentences.length * 0.8;
  }
}

export const nlpService = new NLPService();

server/services/video.ts.content:
interface VideoRenderRequest {
  summary: string;
  audioUrl: string;
  duration: number;
  images: string[];
  title: string;
  theme?: string;
}

interface VideoRenderResult {
  urlMp4: string;
  urlSrt?: string;
  urlThumb: string;
  width: number;
  height: number;
  duration: number;
  size: number;
}

class VideoService {
  private readonly serviceUrl: string;

  constructor() {
    this.serviceUrl = process.env.VIDEO_SERVICE_URL || "http://localhost:8002";
  }

  async checkStatus(): Promise<{ status: string; message?: string }> {
    try {
      const response = await fetch(`${this.serviceUrl}/health`, {
        method: 'GET',
        timeout: 5000
      });

      if (response.ok) {
        return { status: "operational" };
      } else {
        return { status: "degraded", message: `HTTP ${response.status}` };
      }
    } catch (error) {
      return { status: "down", message: error instanceof Error ? error.message : "Unknown error" };
    }
  }

  async renderVideo(request: VideoRenderRequest): Promise<VideoRenderResult> {
    try {
      const payload = {
        summary: request.summary,
        audio_url: request.audioUrl,
        duration: request.duration,
        images: request.images,
        title: request.title,
        theme: request.theme || "news",
        output_format: {
          width: 1920,
          height: 1080,
          fps: 30,
          codec: "h264",
          bitrate: "5M",
        },
        branding: {
          logo_url: process.env.BRAND_LOGO_URL,
          color_primary: "#3b82f6",
          color_secondary: "#1e40af",
          font_family: "Inter",
        },
      };

      const response = await fetch(`${this.serviceUrl}/render`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
        timeout: 300000, // 5 minutes timeout for video rendering
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Video service error: ${response.status} ${response.statusText} - ${errorText}`);
      }

      const result = await response.json();

      return {
        urlMp4: result.video_url,
        urlSrt: result.subtitle_url,
        urlThumb: result.thumbnail_url,
        width: result.width || 1920,
        height: result.height || 1080,
        duration: result.duration,
        size: result.size || 0,
      };
    } catch (error) {
      console.error("Error rendering video:", error);
      throw error;
    }
  }

  async generateThumbnail(videoUrl: string, timestamp = 5): Promise<string> {
    try {
      const payload = {
        video_url: videoUrl,
        timestamp: timestamp,
        width: 1280,
        height: 720,
        format: "jpg",
      };

      const response = await fetch(`${this.serviceUrl}/thumbnail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error(`Thumbnail service error: ${response.status} ${response.statusText}`);
      }

      const result = await response.json();
      return result.thumbnail_url;
    } catch (error) {
      console.error("Error generating thumbnail:", error);
      throw error;
    }
  }

  async getVideoInfo(videoUrl: string): Promise<{ duration: number; width: number; height: number; size: number }> {
    try {
      const payload = { video_url: videoUrl };

      const response = await fetch(`${this.serviceUrl}/info`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error(`Video info service error: ${response.status} ${response.statusText}`);
      }

      const result = await response.json();
      return {
        duration: result.duration,
        width: result.width,
        height: result.height,
        size: result.size,
      };
    } catch (error) {
      console.error("Error getting video info:", error);
      throw error;
    }
  }
}

export const videoService = new VideoService();

server/services/youtube.ts.content:
import type { Job, Video, Publication } from "@shared/schema";
import { storage } from "../storage";

interface YouTubeUploadResult {
  videoId: string;
  status: string;
  publishedAt?: Date;
}

class YouTubeService {
  private readonly clientId: string;
  private readonly clientSecret: string;
  private readonly redirectUri: string;
  private accessToken: string | null = null;
  private refreshToken: string | null = null;

  constructor() {
    this.clientId = process.env.YOUTUBE_CLIENT_ID || "";
    this.clientSecret = process.env.YOUTUBE_CLIENT_SECRET || "";
    this.redirectUri = process.env.YOUTUBE_REDIRECT_URI || "urn:ietf:wg:oauth:2.0:oob";

    if (!this.clientId || !this.clientSecret) {
      console.warn("YouTube API credentials not found. Set YOUTUBE_CLIENT_ID and YOUTUBE_CLIENT_SECRET environment variables.");
    }
  }

  async checkStatus(): Promise<{ status: string; message?: string }> {
    try {
      if (!this.clientId || !this.clientSecret) {
        return { status: "down", message: "Credentials not configured" };
      }

      // Try to get a quota check (this requires auth, so we'll simulate)
      const response = await fetch("https://www.googleapis.com/youtube/v3/channels?part=id&mine=true", {
        headers: {
          'Authorization': `Bearer ${this.accessToken || 'test'}`,
        },
      });

      if (response.status === 401) {
        return { status: "degraded", message: "Authentication required" };
      } else if (response.status === 403) {
        return { status: "degraded", message: "Quota exceeded" };
      } else if (response.ok || response.status === 401) {
        return { status: "operational" };
      } else {
        return { status: "degraded", message: `HTTP ${response.status}` };
      }
    } catch (error) {
      return { status: "down", message: error instanceof Error ? error.message : "Unknown error" };
    }
  }

  async publishVideo(job: Job, video: Video): Promise<Publication> {
    try {
      if (!this.accessToken) {
        await this.refreshAccessToken();
      }

      // Get the summary for video metadata
      const summary = await storage.getSummaryByJobId(job.id);
      const article = await storage.getArticleByJobId(job.id);

      const title = this.generateTitle(article?.title || job.topic);
      const description = this.generateDescription(summary?.text || "", article?.url);
      const tags = this.generateTags(job.topic, job.language);

      // Upload video to YouTube
      const uploadResult = await this.uploadToYouTube(video.urlMp4, {
        title,
        description,
        tags,
        categoryId: "25", // News & Politics
        privacyStatus: "public",
        thumbnail: video.urlThumb,
      });

      // Create publication record
      const publication = await storage.createPublication({
        jobId: job.id,
        platform: "youtube",
        videoId: uploadResult.videoId,
        status: uploadResult.status,
        publishedAt: uploadResult.publishedAt,
      });

      console.log(`Published video to YouTube: https://www.youtube.com/watch?v=${uploadResult.videoId}`);
      return publication;
    } catch (error) {
      console.error("Error publishing to YouTube:", error);

      // Create failed publication record
      const publication = await storage.createPublication({
        jobId: job.id,
        platform: "youtube",
        videoId: "",
        status: "failed",
      });

      throw error;
    }
  }

  private async uploadToYouTube(videoUrl: string, metadata: {
    title: string;
    description: string;
    tags: string[];
    categoryId: string;
    privacyStatus: string;
    thumbnail?: string;
  }): Promise<YouTubeUploadResult> {
    if (!this.accessToken) {
      throw new Error("YouTube access token not available");
    }

    // First, download the video file
    const videoResponse = await fetch(videoUrl);
    if (!videoResponse.ok) {
      throw new Error(`Failed to fetch video: ${videoResponse.statusText}`);
    }

    const videoBlob = await videoResponse.blob();

    // Upload video
    const uploadResponse = await fetch("https://www.googleapis.com/upload/youtube/v3/videos?uploadType=multipart&part=snippet,status", {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${this.accessToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        snippet: {
          title: metadata.title,
          description: metadata.description,
          tags: metadata.tags,
          categoryId: metadata.categoryId,
          defaultLanguage: "en",
          defaultAudioLanguage: "en",
        },
        status: {
          privacyStatus: metadata.privacyStatus,
          embeddable: true,
          license: "youtube",
        },
      }),
    });

    if (!uploadResponse.ok) {
      const errorText = await uploadResponse.text();
      throw new Error(`YouTube upload failed: ${uploadResponse.status} ${uploadResponse.statusText} - ${errorText}`);
    }

    const result = await uploadResponse.json();

    // Upload thumbnail if provided
    if (metadata.thumbnail) {
      await this.uploadThumbnail(result.id, metadata.thumbnail);
    }

    return {
      videoId: result.id,
      status: result.status.uploadStatus,
      publishedAt: result.status.publishAt ? new Date(result.status.publishAt) : new Date(),
    };
  }

  private async uploadThumbnail(videoId: string, thumbnailUrl: string): Promise<void> {
    try {
      const thumbnailResponse = await fetch(thumbnailUrl);
      if (!thumbnailResponse.ok) return;

      const thumbnailBlob = await thumbnailResponse.blob();

      await fetch(`https://www.googleapis.com/upload/youtube/v3/thumbnails/set?videoId=${videoId}`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.accessToken}`,
          'Content-Type': 'image/jpeg',
        },
        body: thumbnailBlob,
      });
    } catch (error) {
      console.error("Error uploading thumbnail:", error);
      // Don't fail the whole upload for thumbnail issues
    }
  }

  private async refreshAccessToken(): Promise<void> {
    if (!this.refreshToken) {
      throw new Error("No refresh token available. Re-authentication required.");
    }

    const response = await fetch("https://oauth2.googleapis.com/token", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: new URLSearchParams({
        client_id: this.clientId,
        client_secret: this.clientSecret,
        refresh_token: this.refreshToken,
        grant_type: 'refresh_token',
      }),
    });

    if (!response.ok) {
      throw new Error(`Token refresh failed: ${response.statusText}`);
    }

    const tokens = await response.json();
    this.accessToken = tokens.access_token;
  }

  private generateTitle(originalTitle: string): string {
    // Ensure title is under 100 characters and compelling
    const cleanTitle = originalTitle.replace(/[^\w\s-]/g, '').trim();
    const maxLength = 90;

    if (cleanTitle.length <= maxLength) {
      return `ðŸ”¥ ${cleanTitle} - Breaking News`;
    }

    return `ðŸ”¥ ${cleanTitle.substring(0, maxLength - 20)}... - Breaking News`;
  }

  private generateDescription(summary: string, sourceUrl?: string): string {
    let description = `${summary}\n\n`;

    description += "ðŸ”” Subscribe for more breaking news updates!\n";
    description += "ðŸ“¢ Like and share if you found this informative!\n\n";

    if (sourceUrl) {
      description += `ðŸ“° Source: ${sourceUrl}\n\n`;
    }

    description += "âš ï¸ This content was automatically generated using AI technology.\n";
    description += "#breakingnews #news #ai #automated";

    return description;
  }

  private generateTags(topic: string, language: string): string[] {
    const baseTags = [
      "breaking news",
      "news",
      "current events",
      "automated news",
      "ai generated",
    ];

    const topicTags = topic.toLowerCase().split(/\s+/).filter(word => word.length > 3);
    const langTags = language === "en" ? ["english news"] : [`${language} news`];

    return [...baseTags, ...topicTags, ...langTags].slice(0, 15);
  }

  setTokens(accessToken: string, refreshToken?: string): void {
    this.accessToken = accessToken;
    if (refreshToken) {
      this.refreshToken = refreshToken;
    }
  }
}

export const youtubeService = new YouTubeService();

client/src/components/job-detail-modal.t.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { 
  CheckCircle, 
  RefreshCw, 
  Upload, 
  Download, 
  X, 
  Play,
  Pause,
  AlertCircle
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { JobWithDetails } from "@/types";

interface JobDetailModalProps {
  job?: JobWithDetails;
  isLoading: boolean;
  error?: Error | null;
  onPublish: () => void;
  publishLoading: boolean;
  onClose?: () => void;
  inline?: boolean;
}

export default function JobDetailModal({ 
  job, 
  isLoading, 
  error,
  onPublish, 
  publishLoading, 
  onClose,
  inline = false 
}: JobDetailModalProps) {
  const steps = [
    { key: "fetch", label: "Fetch", completed: !!job?.article },
    { key: "summarize", label: "Summarize", completed: !!job?.summary },
    { key: "tts", label: "TTS", completed: !!job?.audio },
    { key: "render", label: "Render", completed: !!job?.video },
    { key: "publish", label: "Publish", completed: !!job?.publications?.length },
  ];

  const currentStep = steps.findIndex(step => !step.completed);
  const isRunning = job?.status === "running";
  const isCompleted = job?.status === "completed";
  const isFailed = job?.status === "failed";

  const content = (
    <div className="space-y-6">
      {isLoading && (
        <div className="space-y-4" data-testid="job-detail-loading">
          <Skeleton className="h-8 w-64" />
          <Skeleton className="h-4 w-48" />
          <Skeleton className="h-32 w-full" />
        </div>
      )}

      {error && (
        <div className="flex items-center space-x-2 text-red-600" data-testid="job-detail-error">
          <AlertCircle className="w-5 h-5" />
          <span>Failed to load job details</span>
        </div>
      )}

      {job && (
        <>
          {/* Header */}
          <div className="flex items-center justify-between" data-testid="job-detail-header">
            <div>
              <h2 className="text-xl font-bold text-slate-900" data-testid="text-job-title">
                Job Details - {job.id.slice(0, 8)}...
              </h2>
              <p className="text-slate-600" data-testid="text-job-subtitle">
                {job.topic} â€¢ {job.language} â€¢ {job.targetLength}s
              </p>
            </div>
            {onClose && (
              <Button
                variant="ghost"
                size="sm"
                onClick={onClose}
                data-testid="button-close-modal"
              >
                <X className="w-5 h-5" />
              </Button>
            )}
          </div>

          {/* Pipeline Progress */}
          <div data-testid="pipeline-progress">
            <h3 className="text-lg font-semibold text-slate-900 mb-4">Pipeline Progress</h3>
            <div className="flex items-center space-x-4">
              {steps.map((step, index) => (
                <div key={step.key} className="flex flex-col items-center">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    step.completed 
                      ? "bg-green-100" 
                      : index === currentStep && isRunning 
                        ? "bg-blue-100" 
                        : "bg-slate-100"
                  }`}>
                    {step.completed ? (
                      <CheckCircle className="w-5 h-5 text-green-600" />
                    ) : index === currentStep && isRunning ? (
                      <RefreshCw className="w-5 h-5 text-blue-600 animate-spin" />
                    ) : (
                      <div className={`w-3 h-3 rounded-full ${
                        isFailed && index === currentStep ? "bg-red-400" : "bg-slate-400"
                      }`} />
                    )}
                  </div>
                  <span className="text-xs text-slate-500 mt-1">{step.label}</span>
                  {index < steps.length - 1 && (
                    <div className={`flex-1 h-1 mx-2 rounded ${
                      step.completed ? "bg-green-200" : "bg-slate-200"
                    }`} />
                  )}
                </div>
              ))}
            </div>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Left Column */}
            <div className="space-y-6">
              {/* Summary */}
              {job.summary && (
                <div data-testid="job-summary">
                  <h4 className="text-md font-semibold text-slate-900 mb-3">Generated Summary</h4>
                  <div className="bg-slate-50 rounded-lg p-4">
                    <p className="text-sm text-slate-700 leading-relaxed" data-testid="text-summary">
                      {job.summary.text}
                    </p>
                    <div className="mt-3 text-xs text-slate-500">
                      {job.summary.wordCount} words â€¢ {job.summary.language}
                    </div>
                  </div>
                </div>
              )}

              {/* Audio Preview */}
              {job.audio && (
                <div data-testid="job-audio">
                  <h4 className="text-md font-semibold text-slate-900 mb-3">Audio Preview</h4>
                  <div className="bg-slate-50 rounded-lg p-4">
                    <div className="flex items-center space-x-4">
                      <Button
                        size="sm"
                        className="w-10 h-10 rounded-full p-0"
                        data-testid="button-play-audio"
                      >
                        <Play className="w-4 h-4" />
                      </Button>
                      <div className="flex-1">
                        <Progress value={30} className="h-2" />
                        <div className="flex justify-between text-xs text-slate-500 mt-1">
                          <span>0:15</span>
                          <span>{Math.floor(job.audio.duration / 60)}:{(job.audio.duration % 60).toString().padStart(2, '0')}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Logs */}
              <div data-testid="job-logs">
                <h4 className="text-md font-semibold text-slate-900 mb-3">Execution Logs</h4>
                <div className="bg-slate-900 rounded-lg p-4 h-40 overflow-y-auto">
                  <div className="font-mono text-xs space-y-1 text-green-400">
                    <div>[{new Date().toISOString().slice(0, 19)}] INFO: Job {job.id} started</div>
                    {job.article && (
                      <div className="text-blue-400">[{new Date().toISOString().slice(0, 19)}] INFO: Article fetched successfully</div>
                    )}
                    {job.summary && (
                      <div className="text-green-400">[{new Date().toISOString().slice(0, 19)}] INFO: Summary generated ({job.summary.wordCount} words)</div>
                    )}
                    {job.audio && (
                      <div className="text-green-400">[{new Date().toISOString().slice(0, 19)}] INFO: TTS conversion completed ({job.audio.duration}s duration)</div>
                    )}
                    {job.video && (
                      <div className="text-green-400">[{new Date().toISOString().slice(0, 19)}] INFO: Video rendering completed</div>
                    )}
                    {isRunning && (
                      <div className="text-yellow-400">[{new Date().toISOString().slice(0, 19)}] INFO: Processing in progress ({job.progress}% complete)</div>
                    )}
                    {isFailed && job.error && (
                      <div className="text-red-400">[{new Date().toISOString().slice(0, 19)}] ERROR: {job.error}</div>
                    )}
                  </div>
                </div>
              </div>
            </div>

            {/* Right Column */}
            <div className="space-y-6">
              {/* Video Preview */}
              <div data-testid="job-video">
                <h4 className="text-md font-semibold text-slate-900 mb-3">Video Preview</h4>
                {job.video ? (
                  <div className="bg-slate-100 rounded-lg aspect-video flex items-center justify-center">
                    <video 
                      controls 
                      className="max-w-full max-h-full rounded"
                      poster={job.video.urlThumb}
                      data-testid="video-player"
                    >
                      <source src={job.video.urlMp4} type="video/mp4" />
                      Your browser does not support the video tag.
                    </video>
                  </div>
                ) : (
                  <div className="bg-slate-100 rounded-lg aspect-video flex items-center justify-center">
                    <div className="text-center">
                      <div className="w-12 h-12 bg-slate-300 rounded-lg mx-auto mb-3 flex items-center justify-center">
                        {isRunning ? (
                          <RefreshCw className="w-6 h-6 text-slate-600 animate-spin" />
                        ) : (
                          <Play className="w-6 h-6 text-slate-600" />
                        )}
                      </div>
                      <p className="text-slate-500">
                        {isRunning ? "Video rendering in progress..." : "Video not ready"}
                      </p>
                      {isRunning && (
                        <div className="w-32 h-1 bg-slate-200 rounded-full mx-auto mt-2">
                          <div 
                            className="h-1 bg-blue-600 rounded-full transition-all" 
                            style={{ width: `${job.progress}%` }}
                          />
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>

              {/* Job Information */}
              <div data-testid="job-information">
                <h4 className="text-md font-semibold text-slate-900 mb-3">Job Information</h4>
                <div className="space-y-3">
                  <div className="flex justify-between">
                    <span className="text-sm text-slate-600">Created:</span>
                    <span className="text-sm text-slate-900" data-testid="text-created-date">
                      {formatDistanceToNow(new Date(job.createdAt), { addSuffix: true })}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-slate-600">Source:</span>
                    <span className="text-sm text-slate-900">GNews API</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-slate-600">Language:</span>
                    <span className="text-sm text-slate-900">{job.language}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-slate-600">Target Length:</span>
                    <span className="text-sm text-slate-900">{job.targetLength} seconds</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-slate-600">Auto Publish:</span>
                    <span className="text-sm text-slate-900">
                      {job.autoPublish ? "Enabled" : "Disabled"}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-slate-600">Status:</span>
                    <Badge variant={job.status === 'completed' ? 'default' : job.status === 'failed' ? 'destructive' : 'secondary'}>
                      {job.status}
                    </Badge>
                  </div>
                </div>
              </div>

              {/* Actions */}
              <div className="space-y-3" data-testid="job-actions">
                <Button
                  className="w-full"
                  onClick={onPublish}
                  disabled={!isCompleted || publishLoading || !!job.publications?.length}
                  data-testid="button-publish"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  {publishLoading ? "Publishing..." : 
                   job.publications?.length ? "Published to YouTube" :
                   !isCompleted ? "Publish to YouTube (Processing...)" : 
                   "Publish to YouTube"}
                </Button>

                {job.video && (
                  <Button
                    variant="outline"
                    className="w-full"
                    onClick={() => window.open(job.video?.urlMp4, '_blank')}
                    data-testid="button-download"
                  >
                    <Download className="w-4 h-4 mr-2" />
                    Download Assets
                  </Button>
                )}

                <Button
                  variant="outline"
                  className="w-full text-red-700 border-red-200 hover:bg-red-50"
                  disabled={isCompleted}
                  data-testid="button-cancel"
                >
                  <X className="w-4 h-4 mr-2" />
                  {isCompleted ? "Job Completed" : "Cancel Job"}
                </Button>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );

  if (inline) {
    return <div className="max-w-6xl mx-auto">{content}</div>;
  }

  // Modal version (not used in current implementation but keeping for future use)
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
        <div className="p-6 overflow-y-auto max-h-[90vh]">
          {content}
        </div>
      </div>
    </div>
  );
}

